import React, { useState } from 'react';
import './FaceDetection.css';

const FaceRecognition = () => {
  const [imageUrl, setImageUrl] = useState('');
  const [result, setResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('image', file);

    try {
      setIsLoading(true); // Show the loading animation
      const response = await fetch('http://localhost:8000/face-recognition', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        const { name, imageUrl } = data;
        setResult(name); // Store the recognition result in the component state
        setImageUrl(imageUrl); // Store the image URL in the component state
      } else {
        console.log('Error recognizing face. Status:', response.status);
        const errorData = await response.json();
        console.log('Error message:', errorData.message);
      }
    } catch (error) {
      console.log('Error recognizing face:', error);
    } finally {
      setIsLoading(false); // Hide the loading animation
    }
  };

  return (
    <div className="FaceDetection">
      <h2>Face Recognition</h2>
      {isLoading ? (
        <>
          <br />
          <br />
          <br />
          <div className="loader-wrapper">
            <div className="loader">
              <div className="bar1"></div>
              <div className="bar2"></div>
              <div className="bar3"></div>
              <div className="bar4"></div>
              <div className="bar5"></div>
              <div className="bar6"></div>
            </div>
          </div>
          <br />
        </>
      ) : (
        <>{imageUrl ? (
          <img
            src={imageUrl}
            alt="Detected face"
            style={{ maxWidth: '400px', marginTop: '10px' }}
          />
        ) : (
          <p></p>
        )}
        {result !== '' && <h2>This is: {result}</h2>}
          <br />
          <input type="file" accept="image/*" onChange={handleFileChange} />


        </>
      )}
    </div>
  );
};

export default FaceRecognition;
